import Login from './Login';
import SideBar from './SideBar';
import Content from './Content';
const Body = (props) => {

    const isLoggedIn = props.isLoggedIn
    return (
        <div className=" border flex-1 center items-stretch" >
            {isLoggedIn ? (
            <>
            <SideBar /> 
            <Content />           
            </>):(
            <Login onSubmit = {() =>{}} />
            )}

        </div>
    );
}; 

export default Body;
