import { useState } from 'react';
import Login from './Login';
import SideBar from './SideBar';
import Content from './Content';
const Body = () => {

    const [isLoggedIn,setIsLoggedIn] = useState(true);
    return (
        <div className=" border flex-1 center items-stretch" >
            {isLoggedIn ? (
            <>
            <SideBar /> 
            <Content />           
            </>): <Login />}

        </div>
    );
}; 

export default Body;
